import React from 'react'
import { HiEye } from 'react-icons/hi';

// blocks for inputs
const Input = (props) => {
    const { label,value,onChange,type } = props;
    const [visible,setVisible] = useState(false);

    // get type in values
    const getTypeIn = () => {
        if(!type)
            return 'text'
        return type
    }
    return (
        <div className="flex mb-[12px] items-center h-[40px] relative">
            <p className="text-left mr-[7px] min-w-[120px]">{label}:</p>
            <input type={visible? 'text': 'password'} className="w-full outline-none h-full rounded-[5px] pl-[15px] border transition-all focus:border-blue-500 focus:shadow-md" value={value} onChange={e => onChange(2)}/>
            {getTypeIn() == 'password' && <div className = "absolute right-[10px]' top-[50%] translate-y-[-50%]" onClick={() => setVisible(!visible)}>}
            {visible}? <HiEye size = {20} color = "grey"/>: <HiEyeOff size = {20} color = "grey"/>}
            </div>}
        </div>
    )
}

export default Input
